package pkg01;

public class addtion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 3; // variable  => the place to store the data. Virtual area.
		
		//Type of data
		// Integer (int) 
		// Float (float)
		// Char (char)
		// String (string)
		// Boolean (bool)

		/*
		 * How to populate the value
		 * 
		 * Type name of variables = value;
		 * ex)
		 * int x; ->Store the integer data to java virtual machine as variable x.
		 */
		
		//Assignment
		//name of variable = value;
		//x =3; it means we store the 3 at the variable x
		
		/*
		 *  +
		 *  1. addition
		 *  2. string composition
		 */
		
		int y = 4;
		int sum = x+y;
		int mux = x*y;
		System.out.println(x+ "더하기 "+ y +"는 "+ sum + "입니다.");
		
		System.out.println(x+ "곱하기 "+ y +"는 "+ mux + "입니다.");
		
		
		

	}

}
