public class MyPrint {

	public static void main(String[] args) 
	{
	

		/*
		 *  main method : Main execution  
		 * 	println : Print the string values then \n
		 *  Must be using ""  between ()
		 */

		// TODO Auto-generated method stub
		System.out.println("Hello world!");
		System.out.println("홍길동");
		System.out.println("강\n");
		System.out.println("감");
		System.out.println(); // Kinda doing enter key \n
		System.out.print("nig");
		System.out.println("찬");
		
		// \t  tap 
		// \n  enter
		// \"
		// \'
		// \\
		System.out.println("hello");
		System.out.println("제 이름은 \"홍길동\" 입니다");
		System.out.println("----------------------------");
		System.out.println("이름 \t 국ㄱ어 \t 영어");
		System.out.println("김 \t 80 \t 90");
		System.out.println("조 \t 20 \t15");
		System.out.println("복면가왕 MC \'김성주\'");
		
		System.out.print("\n동해물과 백두산이\n\n 마르고 닳도록\n");
	}
	
	
}
